// ShopContextProvider.js
import React, { createContext, useState } from 'react';
import all_product from '../Components/Assets/all_product';

export const ShopContext = createContext(null);

const getDefaultCart = () => {
    let cart = {};
    all_product.forEach(product => {
        cart[product.id] = { quantity: 0, price: product.new_price }; // Initialize quantity and price
    });
    return cart;
}

const ShopContextProvider = (props) => {
    const [cartItems, setCartItems] = useState(getDefaultCart());

    const addToCart = (itemID, newPrice) => {
        setCartItems(prev => ({
            ...prev,
            [itemID]: {
                quantity: prev[itemID].quantity + 1,
                price: newPrice // Update the price with the new price
            }
        }));
    }

    const removeFromCart = (itemID) => {
        setCartItems(prev => ({
            ...prev,
            [itemID]: {
                quantity: prev[itemID].quantity - 1,
                price: prev[itemID].price // Keep the same price when removing
            }
        }));
    }

    const getTotalCartAmount = () => {
        let totalAmount = 0;
        for (const item in cartItems) {
            totalAmount += cartItems[item].quantity * cartItems[item].price;
        }
        return totalAmount;
    }

    const getTotalCartItems = () => {
        let totalItems = 0;
        for (const item in cartItems) {
            totalItems += cartItems[item].quantity;
        }
        return totalItems;
    }

    const ContextValue = {
        getTotalCartItems,
        getTotalCartAmount,
        all_product,
        cartItems,
        addToCart,
        removeFromCart
    };

    return (
        <ShopContext.Provider value={ContextValue}>
            {props.children}
        </ShopContext.Provider>
    );
}

export default ShopContextProvider;
