import React, { useState } from 'react'
import './CSS/LoginSignup.css'
import {Link} from 'react-router-dom';

const LoginSignup = () => {

  const [state,setState] = useState("Login");


  return (
    <div className='LoginSignup'>
      <div className='LoginSignup-container'>
          <h1>{state}</h1>
          <div className='LoginSignup-fields'>
            {state === "Sing Up"?<input type='text' placeholder='Username' />:<></>}
            <input type='email' placeholder='Email Address'/>
            <input type='password' placeholder='Password' />
          </div>
          <button>Continue</button>  
          {state === "Sign Up"?<p className='LoginSignup-login'>Already have an account? <Link to='/LoginPage'><span>login here</span></Link></p>: <p className='LoginSignup-login'>create an account? <Link to='/LoginPage'><span>click here</span></Link></p>}
        <div className='LoginSignup-agree'>
                <input type='checkbox' name='' id='' />
                <p>By continue, i agree to the terms of use & privacy policy. </p>
        </div>       
      </div>
    </div>
  )
}

export default LoginSignup
