import React, { createContext, useState } from 'react';
import all_product from '../Components/Assets/all_product';

export const ShopContext = createContext(null);

const getDefaultCart = () => {
    let cart = {};
    all_product.forEach(product => {
        cart[product.id] = 0;
    });
    return cart;
}

const ShopContextProvider = (props) => {
    const [cartItems, setCartItems] = useState(getDefaultCart());

    // const addToCart = (itemID) => {
    //     setCartItems((prev) => ({
    //         ...prev, [itemID]: prev[itemID] + 1
    //     }));
    // }

    const addToCart = (itemID, newPrice) => {
        setCartItems(prev => ({
            ...prev,
            [itemID]: {
                quantity: prev[itemID].quantity + 1,
                price: newPrice
            }
        }));
    }


    const removeFromCart = (itemID) => {
        setCartItems((prev) => ({
            ...prev, [itemID]: prev[itemID] - 1
        }));
    }
    const getTotalCartAmount = () => {
        let totalAmount = 0;
        for (const item in cartItems) {
            if (cartItems[item] > 0) {
                let itemInfo = all_product.find((product) => product.id === Number(item));
                totalAmount += itemInfo.new_price * cartItems[item];
            }
        }
        return totalAmount;
    }


    const getTotalCartAmounts = () =>{
        let totalItem = 0;
        for(const item in cartItems){
            if(cartItems[item]>0){
                totalItem += cartItems[item]
            }
        }
        return totalItem;
    }


    console.log("all_product:", all_product); // Add this line

    const ContextValue = {getTotalCartAmounts,getTotalCartAmount, all_product, cartItems, addToCart, removeFromCart };

    return (
        <ShopContext.Provider value={ContextValue}>
            {props.children}
        </ShopContext.Provider>
    )
}

export default ShopContextProvider;
