import React, { useContext } from 'react';
import './productDisplay.css';
import star_icon from "../Assets/star_icon.png";
import { ShopContext } from '../../Context/ShopContext';

const ProductDisplay = (props) => {
    const { Products } = props;
    const { addToCart } = useContext(ShopContext);

    const calculateNewPrice = () => {
        // Calculate new price based on selected weight
        // You should implement the logic for weight selection here
        return Products.new_price; // Assuming no weight selection in this example
    };

    return (
        <div className='productdisplay'>
            {/* Other JSX */}
            <button onClick={() => addToCart(Products.id, calculateNewPrice())}>ADD TO CART</button>
            {/* Other JSX */}
        </div>
    );
};

export default ProductDisplay;
