// ProductDisplay.js
import React, { useState, useContext } from 'react';
import './productDisplay.css';
import { ShopContext } from '../../Context/ShopContext';

const ProductDisplay = (props) => {
    const { Products } = props;
    const { addToCart } = useContext(ShopContext);
    const [selectedWeight, setSelectedWeight] = useState(Products.weights[0]);

    const handleWeightSelection = (weight) => {
        setSelectedWeight(weight);
    };

    const calculatePrice = () => {
        return Products.new_price * selectedWeight.multiplier;
    };

    return (
        <div className='productdisplay'>
            <div className='productdisplay-right'>
                <div className='productdisplay-right-prices'>
                    <div className='productdisplay-right-prices-new'>
                        ${calculatePrice()}
                    </div>
                </div>
                <div className='productdisplay-right-size'>
                    <h1>Select Size</h1>
                    <div className='productdisplay-right-sizes'>
                        {Products.weights.map((weight) => (
                            <div key={weight.label} onClick={() => handleWeightSelection(weight)}>
                                {weight.label}
                            </div>
                        ))}
                    </div>
                </div>
                <button onClick={() => { addToCart(Products.id) }}>ADD TO CART</button>
            </div>
        </div>
    );
};

export default ProductDisplay;
