import React, { useContext, useState } from 'react';
import { ShopContext } from '../../Context/ShopContext';
import './PaymentGateway.css'; // Import CSS file

const PaymentGateway = () => {
    const { totalCartAmount } = useContext(ShopContext);

  return (
    <div className="payment-container">
      <h3>Payment Details</h3>
      <form className="payment-form">
        <input type="text" className="input-field" placeholder="Full Name" />
        <input type="text" className="input-field" placeholder="Mobile Number" />
        <input type="text" className="input-field" placeholder="Address" />
        <input type="text" className="input-field" placeholder="Pincode" />totalCartAmount
        <input type="text" value={`₹${totalCartAmount}`} readOnly />
        <div className="checkbox-container">
          <input type="checkbox" className="checkbox" id="cashOnDelivery" />
          <label htmlFor="cashOnDelivery">Cash on Delivery</label>
        </div>
        <button className="submit-button">Proceed to Pay</button>
      </form>
    </div>
  );
}

export default PaymentGateway;
