import React, { createContext, useEffect, useState } from 'react';

export const ShopContext = createContext(null);

const getDefaultCart = () => {
    let cart = {};
    for (let index = 0; index < 300 + 1; index++) {
        cart[index] = { quantity: 0, price: 0 }; // You might want to adjust the default price
    }
    return cart;
}

const ShopContextProvider = (props) => {
    const [allProducts, setAllProducts] = useState([]);
    const [cartItems, setCartItems] = useState(getDefaultCart());

    useEffect(() => {
        fetch('http://localhost:4000/allproducts')
            .then((response) => response.json())
            .then((data) => setAllProducts(data))
            .catch((error) => console.error('Error fetching products:', error));
    }, []);

    const addToCart = (itemID, newPrice) => {
        setCartItems(prev => ({
            ...prev,
            [itemID]: {
                ...prev[itemID],
                quantity: prev[itemID].quantity + 1,
                price: newPrice
            }
        }));
    }

    const removeFromCart = (itemID) => {
        setCartItems(prev => ({
            ...prev,
            [itemID]: {
                ...prev[itemID],
                quantity: prev[itemID].quantity - 1
            }
        }));
    }

    const getTotalCartAmount = () => {
        let totalAmount = 0;
        for (const item in cartItems) {
            totalAmount += cartItems[item].quantity * cartItems[item].price;
        }
        return totalAmount;
    }

    const getTotalCartAmounts = () => {
        let totalItems = 0;
        for (const item in cartItems) {
            totalItems += cartItems[item].quantity;
        }
        return totalItems;
    }

    const ContextValue = {
        getTotalCartAmounts,
        getTotalCartAmount,
        allProducts,
        cartItems,
        addToCart,
        removeFromCart
    };

    return (
        <ShopContext.Provider value={ContextValue}>
            {allProducts.length > 0 ? props.children : "Loading..."}
        </ShopContext.Provider>
    );
}

export default ShopContextProvider;
